mapper.update:
UPDATE MY_FILELOAD
SET MF_TITLE=#{mfTitle},MF_TITLE=#{mfTitle}, MF_CATEGORY=#{mfCategory}, mfMemo=#{mfMemo} 
MF_FILENAME=#{filename}, MF_NEWFILENAME=#{mfNFilename}, mfRegdate=now()
WHERE MF_SEQ=#{mfSeq}

mapper.select:
select MF_SEQ, MF_CATEGORY, MF_TITLE, MF_REGDATE, MF_MEMO, MF_FILE, MF_FILENAME,  MF_NEWFILENAME
from MY_FILELOAD
where MF_SEQ=#{mfSeq}
