set name=#{name}, profPic="logo.png", profMsg=#{profMsg}, job=#{job}, birthdate=#{birthdate}, address=#{address}
where token=#{token}

update user
set name=#{name}, profMsg=#{profMsg}, job=#{job}, birthdate=#{birthdate}, address=#{address}
where token=#{token}

mapper.select:
select token
from user
where token=#{token}

select auth
from user
where token=#{token}

